SELECT short_name, total_podiums_one, total_podiums as total_podiums_two from ( SELECT short_name, total_podiums as total_podiums_one, driver_two   FROM (SELECT * FROM Teams WHERE (SELECT Drivers.total_podiums FROM Drivers WHERE driver_id=Teams.driver_one) > 5*(SELECT Drivers.total_podiums FROM Drivers WHERE driver_id=Teams.driver_two)   ORDER By short_name) JOIN Drivers ON driver_id=driver_one )  JOIN Drivers ON driver_id=driver_two;
